// Validate status 2xx 
pm.test("[GET]::/api/logistics/pvt/shipping-policies/:id - Status code is 2xx", function () {
   pm.response.to.be.success;
});
// Validate if response header has matching content-type
pm.test("[GET]::/api/logistics/pvt/shipping-policies/:id - Content-Type is application/json", function () {
   pm.expect(pm.response.headers.get("Content-Type")).to.include("application/json");
});
// Validate if response has JSON Body 
pm.test("[GET]::/api/logistics/pvt/shipping-policies/:id - Response has JSON Body", function () {
    pm.response.to.have.jsonBody();
});
// Response Validation
const schema = {"type":"object","description":"Details about a given shipping policy.","properties":{"id":{"type":"string","description":"[Shipping policy](https://help.vtex.com/en/tutorial/shipping-policy--tutorials_140) ID."},"name":{"type":"string","description":"[Shipping policy](https://help.vtex.com/en/tutorial/shipping-policy--tutorials_140) name."},"shippingMethod":{"type":"string","description":"Type of shipping available for this shipping policy (carrier). Options are shown on [shipping simulation](https://help.vtex.com/en/tutorial/shipping-simulation--tutorials_144)."},"weekendAndHolidays":{"type":"object","description":"If the shipping policy includes deliveries on weekends and holidays.","properties":{"saturday":{"type":"boolean","description":"Defines if shipping on Saturdays is valid (`true`) or not (`false`)."},"sunday":{"type":"boolean","description":"Defines if shipping on Sundays is valid (`true`) or not (`false`)."},"holiday":{"type":"boolean","description":"Defines if shipping on holidays is valid (`true`) or not (`false`)."}}},"maxDimension":{"type":"object","description":"Object containing attributes of maximum dimension permitted by the shipping policy (carrier).","properties":{"largestMeasure":{"type":"number","description":"Largest measure acceptable of the package."},"maxMeasureSum":{"type":"number","description":"Limit for the sum of all maximum measures of the package."}}},"numberOfItemsPerShipment":{"type":"integer","description":"Determines the number of items allowed per shipment."},"minimumValueAceptable":{"type":"number","description":"Minimum value accepted by the carrier for delivering."},"maximumValueAceptable":{"type":"number","description":"Maximum value accepted by the carrier for delivering."},"additionalTime":{"type":"string","description":"Additional time configured in `HH:MM:SS` format."},"additionalPrice":{"type":"object","description":"Additional price object.","properties":{"method":{"type":"integer","description":"Additional price method."},"value":{"type":"number","description":"Payment value."}}},"deliveryScheduleSettings":{"type":"object","description":"[Scheduled delivery](https://help.vtex.com/en/tutorial/scheduled-delivery--22g3HAVCGLFiU7xugShOBi) settings.","properties":{"useDeliverySchedule":{"type":"boolean","description":"Defines if the scheduled delivery configuration is active (`true`) or not (`false`)."},"maxRangeDelivery":{"type":"number","description":"Range of days available within a delivery window, for the customer to choose the scheduled delivery. For example, if the configured `maxRangeDelivery` is equal `7`, and the customer buys something on a Tuesday, the options for scheduled delivery will be shown until the following Tuesday (7 days from the purchase day). If no options are available within the `maxRangeDelivery` set, the shipping policy will not be shown on the checkout."},"dayOfWeekForDelivery":{"type":"array","description":"Selected days for delivery, from `0` (Sunday) to `6` (Saturday).","items":{"type":"object","description":"Selected days for delivery objects.","properties":{"dayOfWeek":{"type":"integer","description":"Day of the week, from `0` (Sunday) to `6` (Saturday)."},"deliveryRanges":{"type":"array","description":"Reservation window for scheduled delivery.","items":{"type":"object","description":"Reservation window for scheduled delivery object.","properties":{"startTime":{"type":"string","description":"Start time for day of the week in `hh:mm:ss` format."},"endTime":{"type":"string","description":"End time for day of the week in `hh:mm:ss` format."},"listPrice":{"type":"number","description":"List price for day of the week."},"deliveryCapacity":{"type":["array","null"],"description":"Sets maximum [delivery capacity](https://help.vtex.com/en/tutorial/managing-delivery-capacity--2y217FQZCjD0I1n62yxVcz) for a given reservation window for scheduled delivery.","items":{"type":"object","description":"Object with the maximum delivery capacity for a reservation window.","properties":{"capacityType":{"type":"string","description":"Defines how the delivery capacity is measured for this reservation window, by maximum number of orders (`ORDERS_QUANTITY`) or items (`SKUS_QUANTITY`)."},"maxValue":{"type":"number","description":"Maximum number of orders or SKUs that the store is capable of fulfilling."}}}}}}}}}},"dayOfWeekBlockeds":{"type":["array","null"],"description":"Defines if there are any days of the week that are blocked.","items":{"type":"string","description":"Date and time of the blocked day in [ISO 8601 time zone offset format](https://learn.microsoft.com/en-us/rest/api/storageservices/formatting-datetime-values), as in `YYYY-MM-DDThh:mm:ss`."}}}},"carrierSchedule":{"type":"array","description":"Schedule sent by the carrier to configure the shipping policy.","items":{"type":"object","description":"Carrier schedule for configuring the shipping policy object.","properties":{"dayOfWeek":{"type":"integer","description":"Day of the week from `0` (Sunday) to `6` (Saturday)."},"timeLimit":{"type":"string","description":"Time limit in `HH:MM:SS` format."}}}},"cubicWeightSettings":{"type":"object","description":"Settings for the cubic weight of a package, which accounts for the [package's volume](https://help.vtex.com/en/tutorial/understanding-the-cubic-weight-factor--tutorials_128), and not only weight.","properties":{"volumetricFactor":{"type":"number","description":"Factor that will be used on the cubic weight calculation, usually given by the carrier."},"minimunAcceptableVolumetricWeight":{"type":"number","description":"Packages' minimum acceptable [volumetric weight](https://help.vtex.com/en/tutorial/understanding-the-cubic-weight-factor--tutorials_128)."}}},"modalSettings":{"type":"object","description":"Configurations for the [modal](https://help.vtex.com/en/tutorial/how-does-the-modal-work--tutorials_125), which is the attachement of a specific product to a carrier specialized in delivering that type of product.","properties":{"modals":{"type":"array","description":"Type of special freight.","items":{"type":"string","enum":["CHEMICALS","ELECTRONICS","FURNITURE","GLASS","LIQUID","MATTRESSES","REFRIGERATED","TIRES","WHITE_GOODS","FIREARMS"],"description":"Modal values assigned to the special freight."}},"useOnlyItemsWithDefinedModal":{"type":"boolean","description":"When set as `true`, the modal's configurations will apply only for items with a defined modal, when `false`, the modal configuration will not be set."}}},"businessHourSettings":{"type":"object","description":"Business hours configurations.","properties":{"carrierBusinessHours":{"type":"array","description":"Business hours configurations array for the days of the week.","items":{"type":"object","description":"Business hours configurations for a single day of the week.","properties":{"openingTime":{"type":"string","description":"Opening time in `HH:MM:SS` format."},"closingTime":{"type":"string","description":"Closing time in `HH:MM:SS` format."},"dayOfWeek":{"type":"integer","description":"Day of the week from `0` (Sunday) to `6` (Saturday).","enum":[0,1,2,3,4,5,6]}}}},"isOpenOutsideBusinessHours":{"type":"boolean","description":"Defines if deliveries outside business hours are allowed (`true`) or not (`false`)."}}},"pickupPointsSettings":{"type":"object","description":"[Pickup points](https://help.vtex.com/en/tutorial/pickup-points--2fljn6wLjn8M4lJHA6HP3R) configurations of the shipping policy.","properties":{"pickupPointIds":{"type":"array","description":"Pickup point IDs configured for the shipping policy.","items":{"type":"string","description":"Pickup point ID."}},"pickupPointTags":{"type":"array","description":"Pickup points tags configured for the shipping policy.","items":{"type":"string","description":"Tag identification."}},"sellers":{"type":"array","description":"Sellers associated with [pickup points](https://help.vtex.com/en/tutorial/pickup-points--2fljn6wLjn8M4lJHA6HP3R) related to the shipping policy.","items":{"type":"string","description":"Sellers IDS."}}}},"processingStatus":{"type":"object","description":"Shipping policy processing status.","properties":{"status":{"type":"integer","format":"int32","description":"Shipping policy processing status code."},"errorMessage":{"type":["string","null"],"description":"Error message, when applicable."},"errorsMetadata":{"type":["string","null"],"description":"Error metadata, when applicable."}}},"deliveryChannel":{"type":"string","description":"Defines if the shipping type is `delivery` or `pickup`."},"calculationType":{"type":"integer","description":"Shipping policy calculation type."},"isActive":{"type":"boolean","description":"Defines if the shipping policy is active (`true`) or not (`false`)."},"lastIndexedAt":{"type":["string","null"],"description":"Date and time since the last [indexing](https://help.vtex.com/en/tutorial/understanding-how-indexation-works--tutorials_256), in [ISO 8601 time zone offset format](https://learn.microsoft.com/en-us/rest/api/storageservices/formatting-datetime-values), as in `YYYY-MM-DD hh:mm:ss[.nnnnnnn]+hh:mm`."},"shippingHoursSettings":{"type":"object","description":"Shipping hours settings configurations.","properties":{"shippingHours":{"type":"array","description":"Shipping hours settings array.","items":{"type":"object","description":"Object with details on shipping hours settings.","properties":{"dayOfWeek":{"type":"integer","description":"Day of the week from `0` (Sunday) to `6` (Saturday)."},"openingTime":{"type":"string","description":"Opening time in `hh:mm:ss` format."},"closingTime":{"type":"string","description":"Closing time in `hh:mm:ss` format."}}}},"acceptOrdersOutsideShippingHours":{"type":"boolean","description":"Defines if the configuration that allows accepting orders outside shipping hours is active (`true`) or not (`false`)."}}},"carrierInfo":{"type":"object","description":"[Carrier](https://help.vtex.com/en/tutorial/carriers-on-vtex--7u9duMD5UQa2QQwukAWMcE) information.","properties":{"carrierAccountName":{"type":"string","description":"VTEX store account name related to the shipping policy."},"deliveryAgreementId":{"type":["string","null"],"description":"Delivery agreement ID."},"linkedDocks":{"type":"array","description":"Shipping policy [loading docks](https://help.vtex.com/en/tutorial/loading-dock--5DY8xHEjOLYDVL41Urd5qj) valid for the carrier.","items":{"type":"object","description":"Loading dock object.","properties":{"id":{"type":"string","description":"Loading dock ID."},"name":{"type":"string","description":"Loading dock name."}}}},"readyToUse":{"type":"boolean","description":"Defines if shipping configurations related to the carrier make them ready for shipping (`true`) or not (`false`)."}}}}}

// Validate if response matches JSON schema 
pm.test("[GET]::/api/logistics/pvt/shipping-policies/:id - Schema is valid", function() {
    pm.response.to.have.jsonSchema(schema,{unknownFormats: ["int32", "int64", "float", "double"]});
});

