// Validate status 2xx 
pm.test("[POST]::/api/pricing-hub/prices - Status code is 2xx", function () {
   pm.response.to.be.success;
});
// Validate if response header has matching content-type
pm.test("[POST]::/api/pricing-hub/prices - Content-Type is application/json", function () {
   pm.expect(pm.response.headers.get("Content-Type")).to.include("application/json");
});
// Validate if response has JSON Body 
pm.test("[POST]::/api/pricing-hub/prices - Response has JSON Body", function () {
    pm.response.to.have.jsonBody();
});
// Response Validation
const schema = {"title":"response2","required":["items"],"type":"object","properties":{"items":{"type":"array","items":{"title":"Item1","required":["index","skuId","price","costPrice","listPrice","priceTable","priceValidUntil"],"type":"object","properties":{"index":{"type":"integer","description":"The same index referring to Checkout's cart that was passed to the API"},"skuId":{"type":"string","description":"The same skuId that was passed to the API"},"price":{"type":"number","description":"The price returned by the pricing API that was used by Pricing Hub. It is measured in cents, so 5000 means 50,00 in local currency"},"costPrice":{"type":"number","description":"The cost price returned by the pricing API that was used by Pricing Hub. It is measured in cents, so 5000 means 50,00 in local currency."},"listPrice":{"type":"number","description":"The list price returned by the pricing API that was used by Pricing Hub. It is measured in cents, so 5000 means 50,00 in local currency"},"priceTable":{"type":"string","description":"The price table that was used to price the item"},"priceValidUntil":{"type":"string","description":"The moment up until the price is valid. After that moment, it will be necessary to call the pricing API again. The format of the string is in RFC3339"}}},"description":"List of items and their respective prices applied by Pricing Hub"}}}

// Validate if response matches JSON schema 
pm.test("[POST]::/api/pricing-hub/prices - Schema is valid", function() {
    pm.response.to.have.jsonSchema(schema,{unknownFormats: ["int32", "int64", "float", "double"]});
});

